        return LinearFog(pos);
	#elif CC_USE_FOG == 1
        return ExpFog(pos);
    #elif CC_USE_FOG == 2
        return ExpSquaredFog(pos);
    #elif CC_USE_FOG == 3
        return LayeredFog(pos);
    #endif
    return 1.;
}
