      vec4(a_matWorld1.xyz, 0.0),
      vec4(a_matWorld2.xyz, 0.0),
      vec4(a_matWorld0.w, a_matWorld1.w, a_matWorld2.w, 1.0)
    );
    matWorldIT = matWorld;
  #elif USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id)];
    matWorldIT = matWorld;
  #else
    matWorld = cc_matWorld;
    matWorldIT = cc_matWorldIT;
  #endif
  vec4 worldPos = matWorld * In.position;
  #if USE_WAVE
    vec3 offset;
    vec3 tangent;
    vec3 bitangent;
    gerstnerWaves(worldPos.xyz, waveVisuals.xyz, waveDirections, offset, v_normal, tangent, bitangent);
    worldPos.xyz += offset;
    #if USE_NORMAL_MAP
      v_tangent = tangent;
      v_bitangent = bitangent;
    #endif
  #endif
  v_position = worldPos.xyz;
  v_projPos = cc_matProj * cc_matView * worldPos;
