}
float LayeredFog(vec4 pos) {
    vec4 wPos = pos;
    float fogAtten = cc_fogAdd.z;
    float _FogTop = cc_fogAdd.x;
    float _FogRange = cc_fogAdd.y;
    vec3 camWorldProj = cc_cameraPos.xyz;
    camWorldProj.y = 0.;
